// Holiday & Weekend Check for JobConnect AI
// Check if today is a weekend or holiday

const today = new Date();
const dayOfWeek = today.getDay(); // 0 = Sunday, 6 = Saturday
const todayStr = today.toISOString().split('T')[0];

// Weekend check
if (dayOfWeek === 0 || dayOfWeek === 6) {
  console.log(`üö´ Skipping job discovery - Weekend (${dayOfWeek === 0 ? 'Sunday' : 'Saturday'})`);
  return [];
}

// Holiday check (India & Pakistan 2025)
const holidays = [
  '2025-01-26', // Republic Day (India)
  '2025-08-15', // Independence Day (India)
  '2025-10-02', // Gandhi Jayanti (India)
  '2025-01-01', // New Year
  '2025-05-01', // Labour Day
  '2025-03-23', // Pakistan Day
  '2025-08-14', // Independence Day (Pakistan)
  '2025-09-06', // Defence Day (Pakistan)
  '2025-11-09'  // Iqbal Day (Pakistan)
];

if (holidays.includes(todayStr)) {
  console.log(`üö´ Skipping job discovery - Holiday (${todayStr})`);
  return [];
}

console.log(`‚úÖ Proceeding with job discovery - Business day (${todayStr})`);
return [{ json: { date: todayStr, proceed: true } }];

// Get User ID for Job Discovery
// This node provides the user ID for specific user job discovery

// Replace this with your actual user ID from Supabase
const userId = 'YOUR_USER_ID_HERE'; // REPLACE WITH YOUR ACTUAL USER ID

console.log(`üîç Starting job discovery for user: ${userId}`);

return [{ json: { user_id: userId } }];

// Generate Platform-Specific Search URLs
// Creates different search queries for LinkedIn and Naukri

const items = $input.all();
const searchUrls = [];

if (items.length === 0) {
  console.log('‚ö†Ô∏è No active preferences found for user');
  return [];
}

for (const item of items) {
  const preferences = item.json;

  if (!preferences.keywords || !preferences.location) {
    console.log('‚ö†Ô∏è Skipping preference - missing keywords or location');
    continue;
  }

  const keywords = Array.isArray(preferences.keywords) 
    ? preferences.keywords.join(' ') 
    : preferences.keywords;

  const location = preferences.location;

  // LinkedIn-specific search query
  const linkedinQuery = `${keywords} developer jobs LinkedIn ${location} India`;
  searchUrls.push({
    json: {
      platform: 'linkedin',
      query: linkedinQuery,
      keywords: preferences.keywords,
      location: location,
      userId: preferences.user_id,
      experienceLevel: preferences.experience_level,
      jobType: preferences.job_type,
      preferenceId: preferences.id
    }
  });

  // Naukri-specific search query
  const naukriQuery = `Naukri.com ${keywords} developer jobs ${location}`;
  searchUrls.push({
    json: {
      platform: 'naukri',
      query: naukriQuery,
      keywords: preferences.keywords,
      location: location,
      userId: preferences.user_id,
      experienceLevel: preferences.experience_level,
      jobType: preferences.job_type,
      preferenceId: preferences.id
    }
  });
}

console.log(`üîç Generated ${searchUrls.length} platform-specific search URLs from ${items.length} preferences`);
return searchUrls;

// Parse Platform-Specific Job Results
// Enhanced parsing for LinkedIn and Naukri specific results

const items = $input.all();
const jobs = [];

for (const item of items) {
  const searchData = item.json;
  const serpResults = item.json.organic_results || [];

  if (!serpResults.length) {
    console.log(`‚ö†Ô∏è No results found for ${searchData.platform} search`);
    continue;
  }

  for (const result of serpResults) {
    try {
      // Extract job information from SERP result
      const jobTitle = result.title || '';
      const jobUrl = result.link || '';
      const jobSnippet = result.snippet || '';

      // Platform-specific validation
      let isValidJob = false;

      if (searchData.platform === 'linkedin') {
        // LinkedIn-specific validation
        isValidJob = jobUrl.includes('linkedin.com') || 
                     jobTitle.toLowerCase().includes('linkedin') ||
                     jobTitle.toLowerCase().includes('job') ||
                     jobTitle.toLowerCase().includes('career');
      } else if (searchData.platform === 'naukri') {
        // Naukri-specific validation
        isValidJob = jobUrl.includes('naukri.com') || 
                     jobTitle.toLowerCase().includes('naukri') ||
                     jobTitle.toLowerCase().includes('job') ||
                     jobTitle.toLowerCase().includes('career');
      }

      if (!isValidJob) {
        continue;
      }

      // Extract company name from title or snippet
      const companyMatch = jobTitle.match(/(.*?)\s+(?:job|career|position|role|linkedin|naukri)/i) ||
                          jobSnippet.match(/(.*?)\s+(?:job|career|position|role|linkedin|naukri)/i);
      const company = companyMatch ? companyMatch[1].trim() : 'Unknown Company';

      // Calculate freshness score (0-100)
      const postedTime = new Date();
      const freshnessScore = Math.max(0, 100 - (Date.now() - postedTime.getTime()) / (1000 * 60 * 60));

      // Platform-specific relevance scoring
      let relevanceScore = 75; // Default

      if (searchData.platform === 'linkedin') {
        if (jobUrl.includes('linkedin.com/jobs')) relevanceScore += 15;
        if (jobTitle.toLowerCase().includes('linkedin')) relevanceScore += 10;
      } else if (searchData.platform === 'naukri') {
        if (jobUrl.includes('naukri.com')) relevanceScore += 15;
        if (jobTitle.toLowerCase().includes('naukri')) relevanceScore += 10;
      }

      const job = {
        title: jobTitle,
        company: company,
        location: searchData.location,
        job_url: jobUrl,
        description: jobSnippet,
        platform: searchData.platform,
        posted_time: postedTime.toISOString(),
        keywords_matched: searchData.keywords,
        relevance_score: Math.min(100, relevanceScore),
        freshness_score: Math.round(freshnessScore),
        user_id: searchData.userId,
        experience_level: searchData.experienceLevel,
        job_type: searchData.jobType ? searchData.jobType[0] : 'full-time'
      };

      jobs.push({ json: job });
    } catch (error) {
      console.log(`‚ö†Ô∏è Error processing job result: ${error.message}`);
    }
  }
}

console.log(`üíº Extracted ${jobs.length} platform-specific jobs from SERP results`);
return jobs;

// Log Platform-Specific Job Discovery Results
// Summary of the job discovery process with platform breakdown

const items = $input.all();
const platformBreakdown = {};

// Count jobs by platform
items.forEach(item => {
  const platform = item.json.platform;
  if (!platformBreakdown[platform]) {
    platformBreakdown[platform] = 0;
  }
  platformBreakdown[platform]++;
});

const summary = {
  timestamp: new Date().toISOString(),
  total_jobs_found: items.length,
  platform_breakdown: platformBreakdown,
  platforms_searched: Object.keys(platformBreakdown),
  status: 'success'
};

console.log(`üéâ Platform-specific job discovery completed successfully!`);
console.log(`üìä Total jobs found: ${summary.total_jobs_found}`);
console.log(`üîç Platform breakdown:`, platformBreakdown);
console.log(`üìà Platforms searched: ${summary.platforms_searched.join(', ')}`);

return [{ json: summary }];
