// Generate Search URLs for LinkedIn and Naukri
// Based on user preferences

const items = $input.all();
const searchUrls = [];

for (const item of items) {
  const preferences = item.json;

  if (!preferences.keywords || !preferences.location) {
    continue;
  }

  const keywords = Array.isArray(preferences.keywords) 
    ? preferences.keywords.join(' ') 
    : preferences.keywords;

  const location = preferences.location;

  // LinkedIn search URL
  const linkedinQuery = `${keywords} jobs ${location} site:linkedin.com/jobs`;
  searchUrls.push({
    json: {
      platform: 'linkedin',
      query: linkedinQuery,
      keywords: preferences.keywords,
      location: location,
      userId: preferences.user_id,
      experienceLevel: preferences.experience_level,
      jobType: preferences.job_type
    }
  });

  // Naukri search URL
  const naukriQuery = `${keywords} jobs ${location} site:naukri.com`;
  searchUrls.push({
    json: {
      platform: 'naukri',
      query: naukriQuery,
      keywords: preferences.keywords,
      location: location,
      userId: preferences.user_id,
      experienceLevel: preferences.experience_level,
      jobType: preferences.job_type
    }
  });
}

console.log(`ðŸ” Generated ${searchUrls.length} search URLs`);
return searchUrls;

// Parse and Filter Jobs from SERP Results
// Extract job information and filter by freshness

const items = $input.all();
const allJobs = [];
const now = new Date();
const freshnessHours = 24; // Only jobs from last 24 hours

for (const item of items) {
  const searchData = item.json;
  const serpResults = searchData.organic_results || [];

  for (const result of serpResults) {
    // Skip if not a job posting
    if (!result.link || !result.title) continue;

    // Extract job information
    const job = {
      title: result.title,
      company: extractCompany(result.title, result.snippet),
      location: searchData.location || '',
      job_url: result.link,
      description: result.snippet || '',
      platform: searchData.platform,
      posted_time: new Date().toISOString(), // Default to now
      keywords_matched: searchData.keywords,
      relevance_score: 0,
      freshness_score: 100,
      user_id: searchData.userId,
      experience_level: searchData.experienceLevel,
      job_type: searchData.jobType
    };

    // Try to extract posted time from title/snippet
    const timeMatch = extractPostedTime(result.title + ' ' + result.snippet);
    if (timeMatch) {
      job.posted_time = timeMatch;
      job.freshness_score = calculateFreshnessScore(timeMatch);
    }

    // Only include if fresh enough
    if (job.freshness_score > 0) {
      allJobs.push(job);
    }
  }
}

console.log(`ðŸ“‹ Parsed ${allJobs.length} fresh jobs`);
return allJobs.map(job => ({ json: job }));

// Helper functions
function extractCompany(title, snippet) {
  // Simple extraction - can be enhanced
  const text = (title + ' ' + (snippet || '')).toLowerCase();
  const companyPatterns = [
    /at\s+([a-zA-Z\s&]+?)\s+(?:in|jobs|hiring)/,
    /([a-zA-Z\s&]+?)\s+is\s+hiring/,
    /([a-zA-Z\s&]+?)\s+careers/
  ];

  for (const pattern of companyPatterns) {
    const match = text.match(pattern);
    if (match) return match[1].trim();
  }

  return 'Unknown Company';
}

function extractPostedTime(text) {
  const timePatterns = [
    /(\d+)\s+(?:hours?|hrs?)\s+ago/i,
    /(\d+)\s+(?:days?)\s+ago/i,
    /posted\s+(?:\d+)\s+(?:hours?|days?)\s+ago/i
  ];

  for (const pattern of timePatterns) {
    const match = text.match(pattern);
    if (match) {
      const hours = parseInt(match[1]);
      const postedTime = new Date(Date.now() - (hours * 60 * 60 * 1000));
      return postedTime.toISOString();
    }
  }

  return null;
}

function calculateFreshnessScore(postedTime) {
  const posted = new Date(postedTime);
  const hoursDiff = (now - posted) / (1000 * 60 * 60);
  const score = Math.max(0, 100 - (hoursDiff * 4)); // 4 points per hour
  return Math.round(score);
}

// Calculate Relevance Scores for Jobs
// Based on keywords, location, and user preferences

const items = $input.all();

for (const item of items) {
  const job = item.json;

  // Calculate keyword relevance (60% of total score)
  const keywordScore = calculateKeywordRelevance(job.title + ' ' + job.description, job.keywords_matched);

  // Calculate location relevance (20% of total score)
  const locationScore = calculateLocationRelevance(job.location, job.location);

  // Freshness score (20% of total score)
  const freshnessScore = job.freshness_score * 0.2;

  // Calculate total relevance score
  const totalScore = Math.round(keywordScore + locationScore + freshnessScore);

  job.relevance_score = Math.max(0, Math.min(100, totalScore));
}

console.log(`ðŸŽ¯ Calculated relevance scores for ${items.length} jobs`);
return items;

// Helper functions
function calculateKeywordRelevance(text, keywords) {
  if (!keywords || !Array.isArray(keywords)) return 0;

  const textLower = text.toLowerCase();
  let score = 0;

  for (const keyword of keywords) {
    const keywordLower = keyword.toLowerCase();
    if (textLower.includes(keywordLower)) {
      score += 10; // 10 points per keyword match
    }
  }

  return Math.min(60, score); // Cap at 60 points
}

function calculateLocationRelevance(jobLocation, userLocation) {
  if (!jobLocation || !userLocation) return 0;

  const jobLoc = jobLocation.toLowerCase();
  const userLoc = userLocation.toLowerCase();

  if (jobLoc.includes(userLoc) || userLoc.includes(jobLoc)) {
    return 20; // Full location score
  }

  // Partial match
  const jobWords = jobLoc.split(/[\s,]+/);
  const userWords = userLoc.split(/[\s,]+/);

  for (const jobWord of jobWords) {
    for (const userWord of userWords) {
      if (jobWord.length > 2 && userWord.length > 2 && 
          (jobWord.includes(userWord) || userWord.includes(jobWord))) {
        return 10; // Partial location score
      }
    }
  }

  return 0;
}

